import React from 'react'



const ViewPodcast = () => {


  const [podcasts, setPodcasts] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      let pod = (window.location.pathname)
      let result = await axios.get(`http://localhost:5000/product/getproduct/${pod}`)
      setPodcasts(result.data.payload)
    }
    fetchData();
  }, [])


  return (
    <div>ViewPodcast</div>
  )
}

export default ViewPodcast
