import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react'



const ViewPodcast = () => {

  var audioPlayer

  const [podcasts, setPodcasts] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      let pod = (window.location.pathname.split('/')[1])
      let result = await axios.get(`http://localhost:5000/podcast/${pod}`)
      console.log(result);
      if (result.data.message === "No podcasts found") {
        setPodcasts(null)
      }
      else {
        setPodcasts(result.data.podcasts)
        audioPlayer = document.getElementById("my-audio");
        audioPlayer.src = result.data.podcasts;
      }
    }
    fetchData();
  }, [])


  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const videoRef = useRef(null);


  const handlePlayPause = () => {
    if (videoRef.current && typeof videoRef.current.play === 'function') {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleMuteUnmute = () => {
    if (videoRef.current && typeof videoRef.current.muted === 'boolean') {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };



  return (
    <>
      {podcasts ? (
        <>
          <h1>{podcasts.name}</h1>
          {podcasts.type === "video" ? (
            <>
              <video ref={videoRef} src={podcasts.file} type="" />
              <button onClick={handlePlayPause}>{isPlaying ? 'Pause' : 'Play'}</button>
              <button onClick={handleMuteUnmute}>{isMuted ? 'Unmute' : 'Mute'}</button>
            </>
          ) : (
            <>
              <audio id="my-audio" controls>
                Your browser does not support the audio element.
              </audio>
              <audio controls>
                <source src="https://res.cloudinary.com/dabvvbjsm/video/upload/v1682236891/bkd88d2nhzw9ke7jh5pt.mp3" />
              </audio>
              <h1>{podcasts.file}</h1>
            </>
          )}
        </>
      ) : (
        <div>No podcasts found</div>
      )}
    </>

  )
}

export default ViewPodcast
