import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "./Podcasts.css"
import { Container, Row, Col, Card } from 'react-bootstrap';

const Podcasts = () => {
  const [podcasts, setPodcasts] = useState([]);

  const sendRequest = async () => {
    try {
      const res = await axios.get('http://localhost:5000/podcast/');
      const data = await res.data;
      if (data && data.podcasts) {
        return data;
      }
    } catch (err) {
      console.log(err);
    }
  };


  useEffect(() => {
    sendRequest().then((data) => setPodcasts(data.podcasts));
  }, []);

  return (
    <Container style={{ paddingTop: '150px' }}>
      <Row>
        {
          podcasts.map((podcast) => (
            <div className="podcast-container">


              {/*<h1>{podcast.name}</h1>
            {
              podcast.type === "video" ? (
                <embed src={podcast.file} type="" />
              ) : (
                <audio controls>
                  <source src={podcast.file} type="audio/mpeg" />
                </audio>
              )
            }*/}

              <Col sm={4}>
                <Card>
                  <Card.Body>
                    <Card.Title>From Serial Killers to Demons</Card.Title>
                    <Card.Text>Podcast from Ranveer Allhabadia</Card.Text>
                  </Card.Body>
                </Card>
              </Col>



            </div>
          ))
        }
      </Row>
    </Container>
  );
};

export default Podcasts;
