import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from "react-router-dom";
import './Home.css';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useSelector } from "react-redux";


const Podcasts = () => {
  const [podcasts, setPodcasts] = useState([]);

  const sendRequest = async () => {
    try {
      const res = await axios.get('http://localhost:5000/podcast/');
      const data = await res.data;
      if (data && data.podcasts) {
        return data;
      }
    } catch (err) {
      console.log(err);
    }
  };

  const fav = async (podcast) => {
    alert("added")
    try {
    } catch (err) {
      console.log(err);
    }
  };


  useEffect(() => {
    sendRequest().then((data) => setPodcasts(data.podcasts));
  }, []);

  const isLoggedIn = useSelector(state => state.isLoggedIn);



  return (
    <>
    <Container style={{ paddingTop: '150px' }}>
      <Row className='w-100'>
        {podcasts.map((podcast) => (
          < Col md={4} >
            <div className='px-5 pod'>
              <Link to={`/${podcast.name}`} style={{ textDecoration: 'none' }} >
                <Card>
                  <Card.Body>
                    <Card.Title>{podcast.name}</Card.Title>
                    <Card.Text>{podcast.description}</Card.Text>
                    <div className="d-flex justify-content-between">
                    <Button onClick={(event) => { event.preventDefault(); fav(podcast); }} variant="secondary">Fav</Button>
                    </div>
                  </Card.Body>
                </Card>
              </Link>
            </div>
          </Col>
        ))}
      </Row>
    </Container >
    </>
  );
};

export default Podcasts;
