import axios from 'axios';
import React, { useEffect, useState } from 'react'



const ViewPodcast = () => {


  const [podcasts, setPodcasts] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      let pod = (window.location.pathname.split('/')[1])
      let result = await axios.get(`http://localhost:5000/podcast/${pod}`)
      console.log(result);
      if (result.data.message === "No podcasts found") {
        setPodcasts(null)
      }
      else
        setPodcasts(result.data.podcasts)
    }
    fetchData();
  }, [])


  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const videoRef = useRef(null);


  return (
    <>
      {podcasts ? (
        <>
          <h1>{podcasts.name}</h1>
          {podcasts.type === "video" ? (
            <embed src={podcasts.file} type="" />
          ) : (
            <audio controls>
              <source src={podcasts.file} type="audio/mpeg" />
            </audio>
          )}
        </>
      ) : (
        <div>No podcasts found</div>
      )}
    </>

  )
}

export default ViewPodcast
