import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from "react-router-dom";
import './Home.css';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useSelector } from "react-redux";
import Auth from '../Auth/Auth';

const Podcasts = () => {
  const [podcasts, setPodcasts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredPodcasts, setFilteredPodcasts] = useState([]);

  const sendRequest = async () => {
    try {
      const res = await axios.get('http://localhost:5000/podcast/');
      const data = await res.data;
      if (data && data.podcasts) {
        return data;
      }
    } catch (err) {
      console.log(err);
    }
  };

  const fav = async (podcast) => {
    alert("added")
    try {
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    sendRequest().then((data) => setPodcasts(data.podcasts));
  }, []);

  const isLoggedIn = useSelector(state => state.auth.isLoggedIn);

  useEffect(() => {
    const filtered = podcasts.filter((podcast) => {
      return podcast.name.toLowerCase().includes(searchQuery.toLowerCase());
    });
    setFilteredPodcasts(filtered);
  }, [searchQuery, podcasts]);

  return (
    <>
      {isLoggedIn &&
        <>
          <Container>
            <input type="text" placeholder='Search' value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
            <div className='px-2'></div>
            <Button className='mt-2' variant="secondary">Search</Button>
          </Container>

          <Container className='head' style={{ paddingTop: '50px', paddingLeft: '50px' }}>

            <h1>Popular Podcasts:</h1>

          </Container>
        </>

      }

      {isLoggedIn &&
        <Container style={{ paddingTop: '70px' }}>
          <Row className='w-100'>
            {filteredPodcasts.map((podcast) => (
              <Col md={4} key={podcast.id}>
                <div className='px-5 pod'>
                  <Link to={`/${podcast.name}`} style={{ textDecoration: 'none' }}>
                    <Card>
                      <Card.Body>
                        <Card.Title>{podcast.name}</Card.Title>
                        <Card.Text>{podcast.speaker}</Card.Text>
                        <div className="d-flex justify-content-between">
                          <Button onClick={(event) => { event.preventDefault(); fav(podcast); }} variant="secondary">Fav</Button>
                        </div>
                      </Card.Body>
                    </Card>
                  </Link>
                </div>
              </Col>
            ))}
          </Row>
        </Container>
      }
      {!isLoggedIn &&
        <Auth />
      }
    </>
  );
};

export default Podcasts;
