import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react'



const ViewPodcast = () => {

  let x
  const [podcasts, setPodcasts] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      let pod = (window.location.pathname.split('/')[1])
      let result = await axios.get(`http://localhost:5000/podcast/${pod}`)
      console.log(result);
      if (result.data.message === "No podcasts found") {
        setPodcasts(null)
      }
      else{
        setPodcasts(result.data.podcasts)
        x=podcasts.file
        console.log("this",x);
      }
    }
    fetchData();
  }, [])


  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const videoRef = useRef(null);


  const handlePlayPause = () => {
    if (videoRef.current && typeof videoRef.current.play === 'function') {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleMuteUnmute = () => {
    if (videoRef.current && typeof videoRef.current.muted === 'boolean') {
      videoRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };



  return (
    <>
      {podcasts ? (
        <>
          <h1>{podcasts.name}</h1>
          {podcasts.type === "video" ? (
            <>
              <video ref={videoRef} src={podcasts.file} type="" />
              <button onClick={handlePlayPause}>{isPlaying ? 'Pause' : 'Play'}</button>
              <button onClick={handleMuteUnmute}>{isMuted ? 'Unmute' : 'Mute'}</button>
            </>
          ) : (
            <>
              <audio controls>
                <source src={x} type="audio/mpeg" />
              </audio>
            </>
          )}
        </>
      ) : (
        <div>No podcasts found</div>
      )}
    </>

  )
}

export default ViewPodcast
