import React, { useState, useEffect } from 'react'
import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField, Typography } from '@mui/material'
import axios from "axios"
import { useDispatch, useSelector } from 'react-redux'
import { adminActions } from '../store'
import { Link, useNavigate } from 'react-router-dom'
import Podcasts from './Podcasts/Podcasts'

const Admin = () => {


  const navigate = useNavigate();

  const isAdminLoggedIn = useSelector(state => state.admin.isAdminLoggedIn); // add selector for isAdminLoggedIn

  const dispatch = useDispatch()

  const [inputs, setInputs] = useState({
    name: "",
    email: "",
    password: "",
    podcastName: "",
    podcastDescription: "",
    category: "",
    type: "",
    speaker: "",
    file: ""
  });

  const addPodcast = async () => {
    const res = axios.post(`http://localhost:5000/podcast/add`, {
      name: inputs.podcastName,
      description: inputs.podcastDescription,
      category: inputs.category,
      type: inputs.type,
      speaker: inputs.speaker,
      file: inputs.file
    }).catch(err => console.log(err))

    const data = await res.data
    return data;
  }


  const sendRequest = async (type = "login") => {
    const res = axios.post(`http://localhost:5000/admin/${type}`, {
      name: inputs.name,
      email: inputs.email,
      password: inputs.password
    }).catch(err => console.log(err))

    const data = await res.data
    return data;
  }

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  const [isSignup, setIsSignup] = useState(false)

  const [video, setVideo] = useState("")


  const handleAdd = async () => {
    try {
      const data = new FormData()
      data.append("file", video)
      data.append("upload_preset", "fliprweb")
      data.append("cloud_name", "dabvvbjsm")
  
      const res = await axios.post("https://api.cloudinary.com/v1_1/dabvvbjsm/video/upload", data)
      console.log(res)
      console.log(res.data.secure_url)
      inputs.file=res.data.secure_url 
      await addPodcast()
    } catch (error) {
      console.log(error)
    }
  }
  

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(inputs);
    if (isSignup) {
      sendRequest("signup")
        .then(() => dispatch(adminActions.login()))
        .then(() => navigate("/admin"))
        .then(data => console.log(data))
    }
    else {
      sendRequest()
        .then(() => dispatch(adminActions.login()))
        .then(() => navigate("/admin"))
        .then(data => console.log(data))
    }
  }

  const [openModal, setOpenModal] = useState(false);

  const handleModalOpen = () => {
    setOpenModal(true);
  };

  const handleModalClose = () => {
    setOpenModal(false);
  };

  useEffect(() => {
    // check if admin is logged in on page load
    const adminLoginState = localStorage.getItem('adminLoginState');
    if (adminLoginState === 'true') {
      dispatch(adminActions.login());
    }
  }, [dispatch]);

  useEffect(() => {
    // update localStorage when admin logs in or out
    localStorage.setItem('adminLoginState', isAdminLoggedIn);
  }, [isAdminLoggedIn]);





  return (
    <div>
      <form onSubmit={handleSubmit} >
        {!isAdminLoggedIn &&
          <Box>

            <Typography>{isSignup ? "Signup" : "Login"}</Typography>

            {isSignup && <TextField name="name" onChange={handleChange} value={inputs.name} placeholder='Name' />}

            <TextField name="email" onChange={handleChange} value={inputs.email} placeholder='email' />
            <TextField name="password" onChange={handleChange} value={inputs.password} placeholder='password' />

            <Button type='submit' variant="contained" color="warning">Submit</Button>

            <Button onClick={() => setIsSignup(!isSignup)} variant="contained" color="warning" >
              Change to {isSignup ? "Login" : "SignUp"}
            </Button>


          </Box>
        }
        {
          isAdminLoggedIn &&
          <>
            <Button onClick={handleModalOpen} variant="contained" color="warning">
              Add Podcast
            </Button>
            <Button
              onClick={() => dispatch(adminActions.logout())}
              LinkComponent={Link} to="/admin"
              variant="contained"
              color="warning"
            >
              LogOut
            </Button>
            <Dialog
              onClose={handleModalClose}
              open={openModal}
            >
              <DialogTitle>Add New Podcast</DialogTitle>
              <DialogContent>
                <TextField
                  name="podcastName"
                  onChange={handleChange}
                  value={inputs.podcastName}
                  required
                  fullWidth
                  placeholder="Podcast Name"
                />
                <TextField
                  name="podcastDescription"
                  onChange={handleChange}
                  value={inputs.podcastDescription}
                  required
                  fullWidth
                  placeholder="Podcast Description"
                />
                <TextField
                  name="category"
                  onChange={handleChange}
                  value={inputs.category}
                  required
                  fullWidth
                  placeholder="Category"
                />
                <TextField
                  name="type"
                  onChange={handleChange}
                  value={inputs.type}
                  required
                  fullWidth
                  placeholder="Type (Audio/Video)"
                />
                <TextField
                  name="speaker"
                  onChange={handleChange}
                  value={inputs.speaker}
                  required
                  fullWidth
                  placeholder="Speaker"
                />
                <input
                  type="file"
                  name="video"
                  onChange={(e) => { setVideo(e.target.files[0]) }}
                  required
                />

              </DialogContent>
              <DialogActions>
                <Button onClick={handleModalClose}>Cancel</Button>
                <Button onClick={handleAdd}>Add</Button>
              </DialogActions>
            </Dialog>
            <Podcasts/>
          </>
        }
      </form>

    </div>
  )
}

export default Admin;
