import React, { useState, useEffect } from 'react'
import { AppBar, Box, Button, Tab, Tabs, Toolbar, Typography } from "@mui/material"
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { authActions } from '../../store';
import "./Header.css"
import { Container, Nav, Navbar } from 'react-bootstrap';

const Header = () => {

  const dispatch = useDispatch()

  const isLoggedIn = useSelector(state => state.auth.isLoggedIn);

  const [value, setValue] = useState();

  // Check for login state in local storage or cookies and dispatch the appropriate action to update the state in Redux
  useEffect(() => {
    const loginState = localStorage.getItem('loginState');
    if (loginState === 'true') {
      dispatch(authActions.login());
    }
  }, [dispatch]);

  // Save login state to local storage or cookies when it changes in Redux
  useEffect(() => {
    localStorage.setItem('loginState', isLoggedIn);
  }, [isLoggedIn]);

  return (
    <Navbar bg="dark" variant="dark">
      <Container>
        <Navbar.Brand href="#home">Navbar</Navbar.Brand>

      </Container>


      <Typography variant='h4'>
        Podcasts
      </Typography>
      {isLoggedIn && <Nav className="me-auto">
        <Nav.Link href="#home">Home</Nav.Link>
        <Nav.Link href="#features">Features</Nav.Link>
        <Nav.Link href="#pricing">Pricing</Nav.Link>
      </Nav>
}

{
  !isLoggedIn &&
  <Button
    className='inpu-submit'
    LinkComponent={Link} to="/auth"
  >
    Signup/Login
  </Button>
}

{
  isLoggedIn &&
  <Button
    className='inpu-submit'
    onClick={() => dispatch(authActions.logout())}
    LinkComponent={Link} to="/auth"
  >
    LogOut
  </Button>
}
      </Navbar >
  )
}

export default Header
