import React, { useState } from 'react'
import { Box, Button, TextField, Typography } from '@mui/material'

const Auth = () => {

  const [inputs, setInputs] = useState({
    name: "", email: "", password: ""
  });

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  const [isSignup, setIsSignup] = useState(false)

  return (
    <div>
      <form onSubmit={handleSubmit} >
        <Box>

          <Typography>{isSignup ? "Login" : "SignUp"}</Typography>

          {isSignup && <TextField name="name" onChange={handleChange} value={inputs.name} placeholder='Name' />}

          <TextField name="email" onChange={handleChange} value={inputs.email} placeholder='email' />
          <TextField name="password" onChange={handleChange} value={inputs.password} placeholder='password' />

          <Button variant="contained" color="warning">Submit</Button>

          <Button onClick={() => setIsSignup(!isSignup)} variant="contained" color="warning" >
            Change to {isSignup ? "Login" : "SignUp"}
          </Button>

        </Box>
      </form>
    </div>
  )
}

export default Auth
