import React, { useState, useEffect } from 'react'
import { AppBar, Box, Button, Tab, Tabs, Toolbar, Typography } from "@mui/material"
import { Form, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { authActions } from '../../store';
import { Container, Nav, NavDropdown, Navbar } from "react-bootstrap";

const Header = () => {

  const dispatch = useDispatch()

  const isLoggedIn = useSelector(state => state.auth.isLoggedIn);

  const [value, setValue] = useState();

  // Check for login state in local storage or cookies and dispatch the appropriate action to update the state in Redux
  useEffect(() => {
    const loginState = localStorage.getItem('loginState');
    if (loginState === 'true') {
      dispatch(authActions.login());
    }
  }, [dispatch]);

  // Save login state to local storage or cookies when it changes in Redux
  useEffect(() => {
    localStorage.setItem('loginState', isLoggedIn);
  }, [isLoggedIn]);

  return (
    <div>

<Navbar bg="light" expand="lg">
      <Container fluid>
        <Navbar.Brand href="#">Navbar scroll</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link href="#action1">Home</Nav.Link>
            <Nav.Link href="#action2">Link</Nav.Link>
            <NavDropdown title="Link" id="navbarScrollingDropdown">
              <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action4">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action5">
                Something else here
              </NavDropdown.Item>
            </NavDropdown>
            <Nav.Link href="#" disabled>
              Link
            </Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success">Search</Button>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>


      
    </div>
  )
}

export default Header
