import React, { useState, useEffect } from 'react'
import { Box, Button, TextField, Typography } from '@mui/material'
import axios from "axios"
import { useDispatch, useSelector } from 'react-redux'
import { adminActions } from '../store'
import { Link, useNavigate } from 'react-router-dom'

const Admin = () => {

  const navigate = useNavigate();

  const isAdminLoggedIn = useSelector(state => state.admin.isAdminLoggedIn); // add selector for isAdminLoggedIn

  const dispatch=useDispatch()

  const [inputs, setInputs] = useState({
    name: "", email: "", password: ""
  });

  const sendRequest = async (type="login") => {
    const res = axios.post(`http://localhost:5000/admin/${type}`, {
      name:inputs.name,
      email: inputs.email,
      password: inputs.password
    }).catch(err => console.log(err))

    const data = await res.data
    return data;
  }

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  const [isSignup, setIsSignup] = useState(false)

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(inputs);
    if(isSignup){
      sendRequest("signup")
      .then(()=>dispatch(adminActions.login()))
      .then(()=>navigate("/admin"))
      .then(data=>console.log(data))
    }
    else{
      sendRequest()
      .then(()=>dispatch(adminActions.login()))
      .then(()=>navigate("/admin"))
      .then(data=>console.log(data))
    }
  }

  useEffect(() => {
    // check if admin is logged in on page load
    const adminLoginState = localStorage.getItem('adminLoginState');
    if (adminLoginState === 'true') {
      dispatch(adminActions.login());
    }
  }, [dispatch]);

  useEffect(() => {
    // update localStorage when admin logs in or out
    localStorage.setItem('adminLoginState', isAdminLoggedIn);
  }, [isAdminLoggedIn]);

  return (
    <div>
      <form onSubmit={handleSubmit} >
        { !isAdminLoggedIn &&
          <Box>

          <Typography>{isSignup ? "Signup" : "Login"}</Typography>

          {isSignup && <TextField name="name" onChange={handleChange} value={inputs.name} placeholder='Name' />}

          <TextField name="email" onChange={handleChange} value={inputs.email} placeholder='email' />
          <TextField name="password" onChange={handleChange} value={inputs.password} placeholder='password' />

          <Button type='submit' variant="contained" color="warning">Submit</Button>

          <Button onClick={() => setIsSignup(!isSignup)} variant="contained" color="warning" >
            Change to {isSignup ? "Login" : "SignUp"}
          </Button>

        </Box>
        }
        {
          isAdminLoggedIn &&
          <Button
            onClick={() => dispatch(adminActions.logout())}
            LinkComponent={Link} to="/admin"
            variant="contained"
            color="warning"
          >
            LogOut
          </Button>

          
        }
      </form>
    </div>
  )
}

export default Admin
