import React, { useState, useEffect } from 'react'
import { AppBar, Box, Button, Tab, Tabs, Toolbar, Typography } from "@mui/material"
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { authActions } from '../../store';
import "./Header.css"

const Header = () => {

  const dispatch = useDispatch()

  const isLoggedIn = useSelector(state => state.auth.isLoggedIn);

  const [value, setValue] = useState();

  // Check for login state in local storage or cookies and dispatch the appropriate action to update the state in Redux
  useEffect(() => {
    const loginState = localStorage.getItem('loginState');
    if (loginState === 'true') {
      dispatch(authActions.login());
    }
  }, [dispatch]);

  // Save login state to local storage or cookies when it changes in Redux
  useEffect(() => {
    localStorage.setItem('loginState', isLoggedIn);
  }, [isLoggedIn]);

  return (
    <AppBar position='sticky'>

      <Toolbar>
        <Typography variant='h4'>
          Podcasts
        </Typography>
        {isLoggedIn && <Box display="flex" marginLeft={"auto"} marginRight="auto">
          <Tabs textColor='inherit' value={value} onChange={(e, val) => setValue(val)}>
            <Tab LinkComponent={Link} to="/1" label="1" />
            <Tab LinkComponent={Link} to="/2" label="2" />
            <Tab LinkComponent={Link} to="/3" label="3" />
          </Tabs>
        </Box>}

        {!isLoggedIn &&
          <Button
            className='inpu-submit' 
            LinkComponent={Link} to="/auth"
            variant="contained"
          >
            Signup/Login
          </Button>
        }

        {isLoggedIn &&
          <Button
            className='inpu-submit'
            onClick={() => dispatch(authActions.logout())}
            LinkComponent={Link} to="/auth"
          >
            LogOut
          </Button>
        }
      </Toolbar>
    </AppBar>
  )
}

export default Header
