import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from "react-router-dom";
import './Podcasts.css';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

const Podcasts = () => {
  const [podcasts, setPodcasts] = useState([]);

  const sendRequest = async () => {
    try {
      const res = await axios.get('http://localhost:5000/podcast/');
      const data = await res.data;
      if (data && data.podcasts) {
        return data;
      }
    } catch (err) {
      console.log(err);
    }
  };

  const remove = async (podcast) => {
    try {
      const res = await axios.delete("http://localhost:5000/podcast/remove", {
        data: podcast,
      });
      console.log(res);
      window.location.reload();

    } catch (err) {
      console.log(err);
    }
  };

  navigate


  useEffect(() => {
    sendRequest().then((data) => setPodcasts(data.podcasts));
  }, []);

  const view = (podcast) => {
    
  }

  return (
    <Container style={{ paddingTop: '150px' }}>
      <Row>
        {podcasts.map((podcast) => (
          < Col >
            <Link onClick={() => view(podcast.name)}>
              <Card>
                <Card.Body>
                  <Card.Title>{podcast.name}</Card.Title>
                  <Card.Text>{podcast.description}</Card.Text>
                  <div className="d-flex justify-content-between">
                    <Button variant="primary" className="mr-2">View</Button>
                    <Button onClick={() => remove(podcast)} variant="primary">Remove</Button>
                  </div>
                </Card.Body>
              </Card>
            </Link>
          </Col>
        ))}
      </Row>
    </Container >
  );
};

export default Podcasts;
