import React, { useState } from 'react'
import { Box, Button, TextField, Typography } from '@mui/material'
import axios from "axios"
import { useDispatch, useSelector } from 'react-redux'
import { authActions } from '../../store'
import { Link, useNavigate } from 'react-router-dom'

const Auth = () => {

  const navigate = useNavigate();

  const isLoggedIn = useSelector(state => state.auth.isLoggedIn); // access the isLoggedIn state from the auth slice

  const dispatch = useDispatch()

  const [inputs, setInputs] = useState({
    name: "", email: "", password: ""
  });

  const sendRequest = async (type = "login") => {
    const res = axios.post(`http://localhost:5000/user/${type}`, {
      name: inputs.name,
      email: inputs.email,
      password: inputs.password
    }).catch(err => console.log(err))

    const data = await res.data
    return data;
  }

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  const [isSignup, setIsSignup] = useState(false)

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(inputs);
    if (isSignup) {
      sendRequest("signup")
        .then(() => dispatch(authActions.login()))
        .then(() => navigate("/1"))
        .then(data => console.log(data))
    }
    else {
      sendRequest()
        .then(() => dispatch(authActions.login()))
        .then(() => navigate("/1"))
        .then(data => console.log(data))
    }
  }

  return (
    <div>
      {!isLoggedIn && <form onSubmit={handleSubmit} >
        <Box>

          <div class="top-header">
            <h3>Hello, Again</h3>
            <small>We are happy to have you back.</small>
          </div>









          <Typography>{isSignup ? "Signup" : "Login"}</Typography>

          {isSignup && <TextField name="name" onChange={handleChange} value={inputs.name} placeholder='Name' />}

          <TextField name="email" onChange={handleChange} value={inputs.email} placeholder='email' />
          <TextField name="password" onChange={handleChange} value={inputs.password} placeholder='password' />

          <Button type='submit' variant="contained" color="warning">Submit</Button>

          <Button onClick={() => setIsSignup(!isSignup)} variant="contained" color="warning" >
            Change to {isSignup ? "Login" : "SignUp"}
          </Button>

          <Link to="/admin" target="_blank">Login for Admin</Link>

        </Box>
      </form>
      }
      {isLoggedIn &&
        <div>
          You are succefully logged in
        </div>
      }
    </div>
  )
}

export default Auth
