import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Podcasts.css';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';

const Podcasts = () => {
  const [podcasts, setPodcasts] = useState([]);

  const sendRequest = async () => {
    try {
      const res = await axios.get('http://localhost:5000/podcast/');
      const data = await res.data;
      if (data && data.podcasts) {
        return data;
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    sendRequest().then((data) => setPodcasts(data.podcasts));
  }, []);

  return (
    <Container style={{ paddingTop: '150px' }}>
      <Row>
        {/*<h1>{podcast.name}</h1>
              {
              podcast.type === "video" ? (
                <embed src={podcast.file} type="" />
              ) : (
                <audio controls>
                  <source src={podcast.file} type="audio/mpeg" />
                </audio>
              )
            }*/}
        {podcasts.map((podcast) => (
          < Col md={4} >
            <Card>
              <Card.Body>
                <Card.Title>{podcast.name}</Card.Title>
                <Card.Text>{podcast.description}</Card.Text>
                <div className="d-flex justify-content-between">
                  <Button onClick={() => view(product)}  variant="primary" className="mr-2">View</Button>
                  <Button onClick={() => remove(product)}  variant="primary">Remove</Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container >
  );
};

export default Podcasts;
